	/*
	 * Generate a random person.
	 * @param string $gender 'M' or 'F'.
	 * @return Array of person attributes.
	 */
	public static function person($gender = null){
		$gender = (!$gender) ? static::gender() : $gender;

		return array(
			'gender' => $gender,
			'prefix' => static::prefix($gender),
			'firstname' => static::firstname($gender),
			'lastname' => static::lastname(),
			'street1' => static::street1(),
			'street2' => static::street2(),
			'city' => static::city(),
			'state' => static::state(),
			'zip' => static::zip(),
			'phone' => static::phone()

		);
	}

	/**
	 * Random gender.
	 * @return string Either 'M' or 'F'.
	 */
	public static function gender(){
		return static::pick(array('M','F'));
	}

	/**
	 * Random name prefix.
	 * @param string $gender Either 'M' or 'F';
	 * @return string Name prefix.
	 */
	public static function prefix($gender = null) {
		$gender = (!$gender) ? static::gender() : $gender;
		$prefixes = ('M'=== $gender) ? array('', 'Dr.', 'Mr.', '') : array('','Dr.', 'Mrs.', 'Miss', '');

		return static::pick($prefixes);
	}

	/**
	 * Random lastname.
	 * @return string Last name.
	 */
	public static function lastname() {
		return static::pick(static::$lastnames);
	}

	/**
	 * Random firstname.
	 * @return string First name.
	 */
	public static function firstname($gender = null) {
		$gender = (!$gender) ? static::gender() : $gender;
		$names = ('M'=== $gender) ? static::$males : static::$females;
		return static::pick($names);
	}

	/**
	 * Random line one of street address.
	 * @return string Street address.
	 */
	public static function street1(){
		$number = static::digits(rand(1, 5));
		$street = static::pick(static::$streets);
		return "$number  $street";
	}

	/**
	 * Random line two of street address.
	 * @return string Street address.
	 */
	public static function street2(){
		$number = static::digits(rand(1, 3));
		$street = static::pick(static::$streets2);
		$line = "$street  $number";
		return (static::pick(array(true, false)))? $line : '';
	}

	/**
	 * Random city.
	 * @return string City name.
	 */
	public static function city(){
		return static::pick(static::$cities);
	}

	/**
	 * Random state abbreviation.
	 * @return string Two char state abbreviation.
	 */
	public static function state(){
		$states = array_keys(static::$states);
		return static::pick($states);
	}

	/**
	 * Random five digit zip code.
	 * @return string Five digit zip code.
	 */
	public static function zip(){
		return static::digits(5);
	}

	/**
	 * Random 10 digit phone number.
	 * @return string Phone number.
	 */
	public static function phone(){
		return static::digits(10);
	}

	/**
	 * Random digits.
	 * @param intger $length Number of digits.
	 * @return string Digits.
	 */
	public static function digits($length = 2){
		return static::concat(range(0,9), $length);
	}

	/**
	 * Concate a string of random array elements.
	 * @param array $choices Array of choices used to make up random string.
	 * @param integer $length Number of digits.
	 * @return string Random string.
	 */
	public static function concat($choices, $length = 2){
		$concat = '';
		for($i=1;$i<=$length;$i++){
			$concat .= static::pick($choices);
		}
		return $concat;
	}

	/**
	 * Random pick array element.
	 * @param array $choices Array of elements to pick from.
	 * @return mixed Random array element.
	 */
	public static function pick($choices){
		return $choices[rand(0, sizeof($choices)-1)];
	}
}
